Future<void> _initializePlayer() async {
    if (_isDisposed || !mounted || _isInitializing) return;

    _isInitializing = true;
    _controllerId = 'vlc_${DateTime.now().millisecondsSinceEpoch}';

    setState(() {
      _isInitialized = false;
      _errorMessage = '';
      _isRetrying = false;
      _showPlayerWidget = false;
    });

    try {
      // Clean up previous controller
      await _cleanDisposePlayer();
      if (_isDisposed || !mounted) return;

      // Create controller based on source type
      if (widget.isTorrentStream) {
        await _createTorrentController();
      } else {
        await _createNetworkController();
      }

      if (_vlcController == null || _isDisposed || !mounted) return;

      print('ðŸš€ Initializing VLC controller: $_controllerId');

      // Initialize with shorter timeout
      await _vlcController!.initialize().timeout(
        const Duration(seconds: 15),
        onTimeout:
            () =>
                throw TimeoutException(
                  'VLC initialization timeout',
                  const Duration(seconds: 15),
                ),
      );

      // Wait for controller to be fully ready,
      await Future.delayed(const Duration(milliseconds: 200));

      if (mounted && !_isDisposed && _vlcController != null) {
        // Verify controller is properly initialized
        if (_vlcController!.value.isInitialized) {
          // Setup listeners after successful initialization
          _setupControllerListeners();

          setState(() {
            _isInitialized = true;
            _isRetrying = false;
            _retryCount = 0;
            _errorMessage = '';
            _showPlayerWidget = true;
          });

          print('âœ… VLC controller successfully initialized: $_controllerId');

          // Start playback with delay
          Timer(const Duration(milliseconds: 300), () {
            if (mounted && !_isDisposed && _vlcController != null) {
              try {
                _vlcController!.play();
              } catch (e) {
                print('âš ï¸ Error starting playback: $e');
              }
            }
          });
        } else {
          throw Exception('Controller initialization failed - not initialized');
        }
      }
    } catch (e) {
      print('âŒ Controller initialization error ($_controllerId): $e'); 
      if (mounted && !_isDisposed) {
        _handlePlayerError(e);
      }
    } finally {
      _isInitializing = false;
    }
  }

package com.example.kaspersky_app

import android.content.Context
import androidx.annotation.NonNull
import io.flutter.embedding.android.FlutterActivity
import io.flutter.embedding.engine.FlutterEngine
import io.flutter.plugin.common.MethodChannel
import com.kavsdk.antivirus.EasyMode
import com.kavsdk.antivirus.EasyResult
import com.kavsdk.antivirus.EasyScanner
import com.kavsdk.antivirus.ScanObject
import com.kavsdk.antivirus.EasyListener
import java.util.concurrent.atomic.AtomicBoolean
import android.util.Log
import org.json.JSONArray
import org.json.JSONObject

class MainActivity: FlutterActivity() {
    companion object {
        private const val CHANNEL = "com.yourcompany.yourapp/kaspersky"
        private const val TAG = "KasperskyChannel"
    }

    private var scanThread: Thread? = null
    private val scanning = AtomicBoolean(false)

    override fun configureFlutterEngine(@NonNull flutterEngine: FlutterEngine) {
        super.configureFlutterEngine(flutterEngine)

        flutterEngine?.let { engine ->
            MethodChannel(engine.dartExecutor.binaryMessenger, CHANNEL).setMethodCallHandler { call, result ->
                when (call.method) {
                    "isInitialized" -> {
                        result.success(KasperskyApp.isInitialized())
                    }
                    "startScan" -> {
                        val modeStr = call.argument<String>("mode") ?: "Basic"
                        val mode = try {
                            EasyMode.valueOf(modeStr)
                        } catch (e: IllegalArgumentException) {
                            EasyMode.Basic
                        }

                        if (KasperskyApp.isInitialized()) {
                            if (scanning.compareAndSet(false, true)) {
                                startScan(mode, result)
                            } else {
                                result.error("ALREADY_SCANNING", "A scan is already in progress", null)
                            }
                        } else {
                            result.error("NOT_INITIALIZED", "Kaspersky SDK is not initialized", null)
                        }
                    }
                    "cancelScan" -> {
                        if (scanning.get() && scanThread != null && scanThread!!.isAlive) {
                            scanThread?.interrupt()
                            scanning.set(false)
                            result.success(true)
                        } else {
                            result.success(false)
                        }
                    }
                    else -> {
                        result.notImplemented()
                    }
                }
            }
        }
    }

    private fun startScan(mode: EasyMode, result: MethodChannel.Result) {
        val antivirus = KasperskyApp.getAntivirus()

        if (antivirus == null) {
            scanning.set(false)
            result.error("ANTIVIRUS_NULL", "Antivirus component is null", null)
            return
        }

        scanThread = Thread {
            try {
                Log.d(TAG, "Starting scan with mode: $mode")
                val easyScanner = antivirus.createEasyScanner()
                val eventChannel = flutterEngine?.let { engine ->
                    MethodChannel(engine.dartExecutor.binaryMessenger, "$CHANNEL/events")
                }

                easyScanner.scan(mode, object : EasyListener {
                    override fun onCalculateProgress(filesCalculated: Int) {
                        runOnUiThread {
                            val data = JSONObject().apply {
                                put("event", "calculateProgress")
                                put("filesCalculated", filesCalculated)
                            }
                            eventChannel?.invokeMethod("onEvent", data.toString())
                        }
                    }

                    override fun onScanProgress(filesProcessed: Int, filesTotal: Int) {
                        runOnUiThread {
                            val data = JSONObject().apply {
                                put("event", "scanProgress")
                                put("filesProcessed", filesProcessed)
                                put("filesTotal", filesTotal)
                            }
                            eventChannel?.invokeMethod("onEvent", data.toString())
                        }
                    }

                    override fun onThreatDetected(scanObject: ScanObject) {
                        runOnUiThread {
                            val data = JSONObject().apply {
                                put("event", "threatDetected")
                                put("filename", scanObject.getFilename())
                                put("threatName", scanObject.getVerdictName())
                            }
                            eventChannel?.invokeMethod("onEvent", data.toString())
                        }
                    }
                })

                val scanResult = easyScanner.result

                Log.d(TAG, "Scan finished")
                Log.d(TAG, "Files found: " + scanResult.filesScanned)
                Log.d(TAG, "Files calculated: " + scanResult.filesCount)
                Log.d(TAG, "Objects scanned: " + scanResult.objectsScanned)
                Log.d(TAG, "Objects skipped: " + scanResult.objectsSkipped)
                Log.d(TAG, "Viruses found: " + scanResult.malwareList.size)
                Log.d(TAG, "Riskware found: " + scanResult.riskwareList.size)
                Log.d(TAG, "Rooted: " + scanResult.isRooted)

                val malwareArray = JSONArray()
                for (malware in scanResult.malwareList) {
                    val malwareObj = JSONObject()
                    malwareObj.put("filename", malware.getFilename())
                    malwareObj.put("threatName", malware.getVerdictName())
                    malwareArray.put(malwareObj)
                }

                val riskwareArray = JSONArray()
                for (riskware in scanResult.riskwareList) {
                    val riskwareObj = JSONObject()
                    riskwareObj.put("filename", riskware.getFilename())
                    riskwareObj.put("threatName", riskware.getVerdictName())
                    riskwareArray.put(riskwareObj)
                }

                val resultJson = JSONObject().apply {
                    put("filesScanned", scanResult.filesScanned)
                    put("filesCount", scanResult.filesCount)
                    put("objectsScanned", scanResult.objectsScanned)
                    put("objectsSkipped", scanResult.objectsSkipped)
                    put("malwareCount", scanResult.malwareList.size)
                    put("riskwareCount", scanResult.riskwareList.size)
                    put("isRooted", scanResult.isRooted)
                    put("malwareList", malwareArray)
                    put("riskwareList", riskwareArray)
                }

                runOnUiThread {
                    scanning.set(false)
                    result.success(resultJson.toString())
                }

            } catch (e: Exception) {
                Log.e(TAG, "Scan error: ${e.message}", e)
                runOnUiThread {
                    scanning.set(false)
                    result.error("SCAN_ERROR", e.message, e.stackTraceToString())
                }
            }
        }

        scanThread?.start()
    }
}
