import { Request, Response } from "express";
import bcrypt from "bcrypt";
import pool from "../models/db";
import jwt from "jsonwebtoken";

const SALT_ROUNDS = 10;
const JWT_SECRET = process.env.JWT_SECRET || 'rudrasecreatkey';

export const register = async (req: Request, res: Response) => {
    // 1. Get username, password, and email from the request body
    // 2. Insert the data into the database
    // 3. Return a success message and user data
    const { username, email, password } = req.body;
    try {
        // Hash the password
        const hashedPassword = await bcrypt.hash(password, SALT_ROUNDS);
        const result = await pool.query(
            'INSERT INTO users (username, email, password) VALUES ($1, $2, $3) RETURNING *',
            [username, email, hashedPassword]
        );
        const user = result.rows[0];
        // res.status(201).json({
        //     message: 'User registered successfully', user
        // });
        res.status(201).json({ user });
    } catch (err: any) {
        res.status(500).json({
            error: 'Failed to register user',
            details: err.message,
        });
    }
}

export const login = async (req: Request, res: Response): Promise<any> => {
    // 1. Get email, password
    // 2. Verify if the email exists in the database
    // 3. Compare password -> 'invalid credentials'
    // 4. Return token
    const { email, password } = req.body;
    try {
        const result = await pool.query(
            'SELECT * FROM users WHERE email = $1',
            [email]
        );
        const user = result.rows[0];
        if (!user) {
            return res.status(404).json({
                error: 'User not found',
            });
        }
        const isMatch = await bcrypt.compare(password, user.password);
        if (!isMatch) {
            return res.status(400).json({
                error: 'Invalid credentials',
            });
        }
        const token = jwt.sign({ id: user.id }, JWT_SECRET, { expiresIn: '10h' });
        let finalResult = { ...user, token } //1
        res.json({ user: finalResult }); //2 He added these two lines to get the complete user data as response.
    } catch (err: any) {
        res.status(500).json({
            error: 'Failed to login',
            details: err.message,
        });
    }
}

