import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:http/http.dart' as http;
import 'package:crypto/crypto.dart';

/// A test page for verifying HotelBeds Content API integration.
/// This page sends a GET request to the Content API endpoint and displays the result.
///
/// This file is part of the **API presentation** layer. In a clean architecture,
/// the API integration logic belongs in the **infrastructure** layer (e.g. in service classes),
/// while this page lives in the presentation layer. Separating these concerns helps with
/// maintainability and makes it easier to replace the API later if needed.
class ContentApiTestPage extends StatefulWidget {
  const ContentApiTestPage({super.key});

  @override
  State<ContentApiTestPage> createState() => _ContentApiTestPageState();
}

class _ContentApiTestPageState extends State<ContentApiTestPage> {
  String? _responseText;
  bool _isLoading = false;

  // Replace these with your actual test API key and API secret from HotelBeds.
  final String _apiKey = (API_key_here);
  final String _apiSecret = (API_secret_here);

  // Generates a SHA256 X-Signature by concatenating the API key, secret, and current timestamp (in seconds).
  String generateSignature() {
    final timestamp =
        (DateTime.now().millisecondsSinceEpoch / 1000).round().toString();
    final stringToSign = '$_apiKey$_apiSecret$timestamp';
    final bytes = utf8.encode(stringToSign);
    final digest = sha256.convert(bytes);
    return digest.toString();
  }

  // Sends an HTTP GET request to the Content API endpoint, using the proper headers.
  // Prints and displays the JSON response.
  Future<void> fetchContentData() async {
    setState(() {
      _isLoading = true;
      _responseText = null;
    });

    // Build the URI for the Content API endpoint.
    // Here we use the "hotels" endpoint from the HotelBeds Content API.
    final uri =
        Uri.parse("https://api.test.hotelbeds.com/content-api/1.0/hotels");

    // Create headers including Api-key and X-Signature.
    final headers = {
      'Accept': 'application/json',
      'Api-key': _apiKey,
      'X-Signature': generateSignature(),
    };

    try {
      final response = await http.get(uri, headers: headers);
      if (response.statusCode == 200) {
        setState(() {
          _responseText = response.body;
        });
        print("Content API Response: ${response.body}");
      } else {
        setState(() {
          _responseText =
              "Error: ${response.statusCode} ${response.reasonPhrase}";
        });
        print(
            "Error fetching content: ${response.statusCode} ${response.reasonPhrase}");
      }
    } catch (e) {
      setState(() {
        _responseText = "Error: $e";
      });
      print("Exception during fetching content: $e");
    }
    setState(() {
      _isLoading = false;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text("Content API Test")),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.stretch,
          children: [
            // A button to trigger the API call.
            ElevatedButton(
              onPressed: fetchContentData,
              child: const Text("Fetch Content Data"),
            ),
            const SizedBox(height: 16),
            // Show loading indicator while fetching.
            if (_isLoading) const Center(child: CircularProgressIndicator()),
            // Display the API response text (or error message) in a scrollable area.
            if (_responseText != null)
              Expanded(
                  child: SingleChildScrollView(child: Text(_responseText!))),
          ],
        ),
      ),
    );
  }
}

