  final List<dynamic> activities;

  const Photo({
    required this.canLogin,
    required this.canRegister,
    required this.activities,
  });

  factory Photo.fromJson(Map<String, dynamic> json) {
    return Photo(
      canLogin: json['canLogin'] as bool,
