class AuthenticationBloc
    extends Bloc<AuthenticationEvent, AuthenticationState> {
  AuthenticationBloc(this.registerUseCase, this.refreshTokenUseCase)
      : super(AuthenticationState.initial()) {
    on<Register>(onRegister);
  }
  final RegisterUseCase registerUseCase;
  final RefreshTokenUseCase refreshTokenUseCase;

  Future<void> onRegister(
      Register event, Emitter<AuthenticationState> emit) async {
    //  await HiveStorage().clearBox();
    emit(AuthenticationState(state: StateStatus.loading));
    if (event.isConnected) {
      bool isValidUser = false;
      UserData? storageUserData = await getUserStorage(event);
      final now = DateTime.now();
      if (storageUserData == null) {
        await HiveStorage().clearBox();
        // await HiveStorage().putLastDate(date: now);
        isValidUser = false;
      } else {
        final lastDate = await HiveStorage().getLastDate();
        if (lastDate != null) {
          if (lastDate.add(const Duration(days: 15)).isBefore(now)) {
            await HiveStorage().clearBox();
            isValidUser = false;
            // await HiveStorage().putLastDate(date: now);
          } else {
            isValidUser = true;
          }
        } else {
          await HiveStorage().clearBox();
          isValidUser = false;
          // await HiveStorage().putLastDate(date: now);
        }
      }
      String? storageFirebaseToken = await getFirebaseToken();
      if (isValidUser && storageFirebaseToken != null) {
        if (event.registerRequest.firebaseToken != storageFirebaseToken) {
          Completer<void> completer = Completer<void>();
          try {
            RefreshTokenRequest refreshTokenRequest = RefreshTokenRequest(
                firebaseToken: event.registerRequest.firebaseToken);
            Endpoints.token = storageUserData!.token!;
            String? token;
            final result =
                await refreshTokenUseCase.call(params: refreshTokenRequest);
            result.fold((l) {
              emit(state.copyWith(
                  state: StateStatus.success, userData: storageUserData));
              completer.complete();
            }, (r) async {
              token = r;
              await HiveStorage().putFirebaseToken(
                  firebaseToken: event.registerRequest.firebaseToken);
              storageUserData = storageUserData!.copyWith(token: token);
              await HiveStorage().putUserData(
                  userData:
                      UserDateHiveMapper().toHiveUserData(storageUserData!));
              emit(state.copyWith(
                  state: StateStatus.success, userData: storageUserData));

              completer.complete();
            });
          } catch (e) {
            print(e);
            emit(state.copyWith(
                state: StateStatus.success, userData: storageUserData));
            completer.complete();
          }
          await completer.future;
        } else {
          emit(state.copyWith(
              state: StateStatus.success, userData: storageUserData));
        }
      } else {
        Completer<void> completer = Completer<void>();
        try {
          UserData? userData;
          final result =
              await registerUseCase.call(params: event.registerRequest);
          result.fold((l) {
            emit(state.copyWith(state: StateStatus.error));
            completer.complete();
          }, (r) async {
            userData = r;

            await HiveStorage()
                .putFirebaseToken(
                    firebaseToken: event.registerRequest.firebaseToken)
                .whenComplete(() {
              return HiveStorage().putLastDate(date: now);
            }).whenComplete(() {
              return HiveStorage().putUserData(
                  userData: UserDateHiveMapper().toHiveUserData(userData!));
            });

            emit(
                state.copyWith(state: StateStatus.success, userData: userData));
            completer.complete();
          });
        } catch (e) {
          emit(state.copyWith(state: StateStatus.error));
          completer.complete();
        }
        await completer.future;
      }
    } else {
      await getStorageUserData(event, emit);
    }
  }

  Future<UserData?> getUserStorage(Register event) async {
    UserData? storageUserData = await HiveStorage().getUserData();
    return storageUserData != null
        ? storageUserData.user!.msisdn == event.registerRequest.msisdn
            ? storageUserData
            : null
        : null;
  }

  Future<String?> getFirebaseToken() async {
    String? storageFirebaseToken = await HiveStorage().getFirebaseToken();
    return storageFirebaseToken;
  }

  Future<void> getStorageUserData(
      Register event, Emitter<AuthenticationState> emit) async {
    Completer<void> completer = Completer<void>();
    UserData? storageUserData = await getUserStorage(event);
    final now = DateTime.now();
    if (storageUserData != null) {
      DateTime? lastDate = await HiveStorage().getLastDate();
      if (lastDate != null) {
        if (lastDate.add(const Duration(days: 15)).isBefore(now)) {
          await HiveStorage().clearBox();
          emit(state.copyWith(state: StateStatus.error));
          completer.complete();
        } else {
          emit(state.copyWith(
              state: StateStatus.success, userData: storageUserData));
          completer.complete();
        }
      } else {
        await HiveStorage().clearBox();
        emit(state.copyWith(state: StateStatus.error));
        completer.complete();
      }
    } else {
      await HiveStorage().clearBox();
      emit(state.copyWith(state: StateStatus.error));
      completer.complete();
    }
    await completer.future;
  }
}
