extension WidgetTestExtensions on WidgetTester {
  Future<MultiProvider> createRootWidgetAndPump({
    required Widget body,
    AppProvider? appProvider,
    AuthExpireState? authExpireState,
    ACLCubit? aclCubit,
    AuthCubit? authCubit,
    DSMProvider? dsmProvider,
    V5SearchQueryReq? query,
    V5FlightCubit? v5FlightCubit,
    BookingCubit? bookingCubit,
    FlightSearchTimerState? flightSearchTimerState,
    V5SelectedFLightsState? selectedFlights,
    AddOnProviderState? addOnProvider,
    Map<String, WidgetBuilder> routes = const <String, WidgetBuilder>{},
  }) async {
    final observers = <NavigatorObserver>[];

    final root = MultiProvider(
      providers: [
        BlocProvider(
          create: (_) => aclCubit ?? ACLCubit()
            ..emit(
              ACLState(
                acl: ACLFetchState(),
                fetchGeoLocation: ACLGeoLocationState(),
                fetchOriginAirport: ACLOriginAirportState(),
              ),
            ),
        ),
        BlocProvider(
          create: (_) => v5FlightCubit ?? V5FlightCubit()
            ..emit(
              V5FlightState(
                rules: V5FlightFareRules(),
                search: V5FlightSearchState(),
                calenderFare: V5FlightFareCalenderState(),
                query: query,
              ),
            ),
        ),
        BlocProvider(
          create: (_) => bookingCubit ?? BookingCubit()
            ..emit(
              BookingState(
                initiate: BookingInitiateState(),
                initiateV4: BookingInitiateV4State(),
                // can add more default states if needed
              ),
            ),
        ),
        BlocProvider(
          create: (_) => authCubit ?? AuthCubit()
            ..emit(
              AuthState(
                  // can add default data in states if needed
                  ),
            ),
        ),
        ChangeNotifierProvider(
          create: (_) => authExpireState ?? AuthExpireState(),
        ),
        ChangeNotifierProvider(
          create: (_) => appProvider ?? AppProvider(),
        ),
        ChangeNotifierProvider(
          create: (_) => selectedFlights ?? V5SelectedFLightsState(),
        ),
        ChangeNotifierProvider(
          create: (_) => dsmProvider ?? DSMProvider(),
        ),
        ChangeNotifierProvider(
          create: (_) => flightSearchTimerState ?? FlightSearchTimerState(),
        ),
        ChangeNotifierProvider(
          create: (_) => addOnProvider ?? AddOnProviderState(),
        ),
      ],
      child: MaterialApp(
        navigatorObservers: observers,
        debugShowCheckedModeBanner: false,
        locale: appProvider?.activeLocale,
        supportedLocales: AppProvider.locales,
        localizationsDelegates: [
          AppLocalizations.delegate,
          GlobalMaterialLocalizations.delegate,
          GlobalWidgetsLocalizations.delegate,
          CountryLocalizations.delegate
        ],
        localeResolutionCallback: (locale, supportedLocales) {
          for (var supportedLocale in supportedLocales) {
            if (locale != null &&
                supportedLocale.languageCode == locale.languageCode &&
                supportedLocale.countryCode == locale.countryCode) {
              return supportedLocale;
            }
          }
          return supportedLocales.first;
        },
        home: Scaffold(body: body),
        routes: routes,
      ),
    );
    await pumpWidget(root);
    return root;
  }

  Future<void> setupInitConfigs(
    WidgetTestBody body, {
    Size physicalSize = defaultPhoneSize,
    double devicePixelRatio = defaultDevicePixelRatio,
  }) async {
    await this.pumpWidget(const MaterialApp());
    final context = this.element(find.byType(MaterialApp));

    /// Needed throughout our app, hence mocking the
    /// context here for each test as a common function here
    App.init(context);

    // Satisfy Flutter Flavors
    AppFlavor.init(PackageInfo(
      appName: 'appName',
      packageName: 'appNamePackage',
      version: '0.7.5',
      buildNumber: '100',
    ));
    await body(this);
  }

class ScreenLayout extends StatelessWidget {
  final bool showBackButton;
  final VoidCallback? onBackPressed;
  final Future<bool> Function()? onWillPopScope;
  final Widget? title;
  final Widget? trailing;
  final bool showHeader;

  final Widget? header;
  final Widget? extendedHeader;
  final Widget? body;
  final Widget? footer;

  final Widget? bottomNavigationBar;
  final Widget? floatingActionButton;
  final Color? scaffoldBackgroundColor;
  final bool showFooter;
  final bool fromSearch;
  final List<Widget>? overlayBuilders;

  final EdgeInsets padding;
  const ScreenLayout(
      {Key? key,
      this.header,
      this.overlayBuilders,
      this.title,
      this.showHeader = true,
      this.showFooter = false,
      this.fromSearch = false,
      this.body = const SizedBox(),
      this.footer,
      this.showBackButton = true,
      this.onBackPressed,
      this.onWillPopScope,
      this.trailing,
      this.padding = EdgeInsets.zero,
      this.bottomNavigationBar,
      this.floatingActionButton,
      this.scaffoldBackgroundColor,
      this.extendedHeader})
      : super(key: key);

  @override
  Widget build(BuildContext context) {
    App.init(context);
    final roles = AuthCubit.cubit(context, true).state.data?.data?.role;
    final isPartnerRole =
        roles != null && roles.contains(SastaWalletStatics.partner);
    final isLoggedIn = AuthCubit.cubit(context, true).repository.isLoggedIn;
    final state = DSMProvider.state(context);
    final dsmProvider = DSMProvider.state(context, true);

    return AnnotatedRegion<SystemUiOverlayStyle>(
      value: SystemUiOverlayStyle(
        statusBarColor: AppTheme.c.gray1,
        statusBarBrightness: Brightness.light, // for IOS
        statusBarIconBrightness: Brightness.dark, // for Android
      ),
