        body: SafeArea(
          top: true,
          bottom: false,
          child: Stack(
            children: [
              Column(
                mainAxisSize: MainAxisSize.min,
                children: [
                  if (showHeader)
                    header ??
                        Container(
                          decoration: BoxDecoration(
                            color: AppTheme.c.gray1,
