                              if (trailing != null) ...[
                                Space.xm,
                                trailing!,
                              ] else ...[
                                Space.xm,
                                Row(
                                  children: [
                                    GestureDetector(
                                      onTap: () async {
                                        await showModalBottomSheet(
                                          context: context,
                                          backgroundColor:
                                              Color.fromRGBO(0, 0, 0, 0),
                                          isDismissible: false,
                                          enableDrag: false,
                                          isScrollControlled: true,
                                          constraints: BoxConstraints(
                                            maxHeight: 350,
