class PageContent extends StatelessWidget {
  final String? title;
  final Widget? titleWidget;
  final EdgeInsets? titlePadding;
  final List<Widget> children;
  final EdgeInsets? padding;
  final MainAxisSize columnSize;
  final MainAxisAlignment verticalAlignment;
  final CrossAxisAlignment horizontalAlignment;
  final bool safeAreaTop;
  final bool safeAreaBottom;
  final bool scrollable;

  const PageContent(
      {super.key,
      this.title,
      this.children = const [],
      this.padding,
      this.titlePadding,
      this.verticalAlignment = MainAxisAlignment.start,
      this.horizontalAlignment = CrossAxisAlignment.center,
      this.columnSize = MainAxisSize.max,
      this.safeAreaTop = true,
      this.scrollable = true,
      this.safeAreaBottom = false})
      : titleWidget = null;

  const PageContent.withTitleWidget(
      {super.key,
      this.titleWidget,
      this.titlePadding,
      this.children = const [],
      this.padding,
      this.verticalAlignment = MainAxisAlignment.start,
      this.horizontalAlignment = CrossAxisAlignment.center,
      this.columnSize = MainAxisSize.max,
      this.safeAreaTop = true,
      this.scrollable = true,
      this.safeAreaBottom = false})
      : title = null;

  @override
  Widget build(BuildContext context) {
    return SafeArea(
        top: safeAreaTop,
        bottom: safeAreaBottom,
        child: scrollable
            ? SingleChildScrollView(
                child: Column(
                  mainAxisAlignment: verticalAlignment,
                  crossAxisAlignment: horizontalAlignment,
                  mainAxisSize: columnSize,
            children: [
              titleWidget != null
                  ? PageTitle.withTitleWidget(
                      titleWidget,
                      padding: titlePadding,
                    )
                  : title != null
                      ? PageTitle(
                          title,
                          padding: titlePadding,
                        )
                      : const SizedBox.shrink(),
              for (var child in children)
                Padding(
                  padding: padding != null
                      ? padding!
                      : comfortableListChildren,
                  child: child,
                ),
            ],
                ),
              )
            : Column(
                mainAxisAlignment: verticalAlignment,
                crossAxisAlignment: horizontalAlignment,
                mainAxisSize: columnSize,
                children: [
                  titleWidget != null
                      ? PageTitle.withTitleWidget(
                          titleWidget,
                          padding: titlePadding,
                        )
                      : title != null
                          ? PageTitle(
                              title,
                              padding: titlePadding,
                            )
                          : const SizedBox.shrink(),
                  for (var child in children)
                    Padding(
                      padding:
                          padding != null ? padding! : comfortableListChildren,
                      child: child,
                    ),
                ],
              ));
  }
}

