Uint8List rsaSign(RSAPrivateKey privateKey, String dataToSign) {

  final signer = RSASigner(SHA256Digest(), '0609608648016503040201'); // 

  signer.init(true, PrivateKeyParameter<RSAPrivateKey>(privateKey)); // true=sign

  final sig = signer.generateSignature(utf8.encode(dataToSign));  

  return sig.bytes;
}

bool rsaVerify(

    RSAPublicKey publicKey, String signedData, Uint8List signature) { 

  final sig = RSASignature(signature);

  final verifier = RSASigner(SHA256Digest(), '0609608648016503040201'); 

  verifier.init(false, PublicKeyParameter<RSAPublicKey>(publicKey)); // false=verify

  try {
    return verifier.verifySignature(utf8.encode(signedData), sig);
  } on ArgumentError {
    return false; 
  }
}


