class SpecialTextWidget extends StatefulWidget {
  final bool _shouldEdit;

  const SpecialTextWidget({
    super.key,
    bool shouldEdit = false,
  }) : _shouldEdit = shouldEdit;

  @override
  State<SpecialTextWidget> createState() => SpecialTextWidgetState();
}

class SpecialTextWidgetState extends State<SpecialTextWidget> {
  late bool _isInitialized;
  late bool _isEnabled;
  String? _data;
  TextStyle? _style;

  @visibleForTesting
  bool get isEnabled => _isEnabled;

  @override
  void initState() {
    _isInitialized = false;
    _isEnabled = false;
    super.initState();
  }

  @override
  void didChangeDependencies() {
    if (!_isInitialized) {
      initialize();
      _isInitialized = true;
    }
    super.didChangeDependencies();
  }

  Future<void> initialize() async {
    await onLoad(
      fontFamily: 'Roboto',
      fontSize: 14.0,
      fontWeight: FontWeight.normal,
      fontStyle: FontStyle.normal,
      color: Colors.black,
      isEnabled: true,
      data: 'Hello',
    );
    if (widget._shouldEdit) {
      await onEdit(
        fontFamily: 'Open Sans',
        fontSize: 16.0,
        fontWeight: FontWeight.bold,
        fontStyle: FontStyle.italic,
        color: Colors.white,
        isEnabled: false,
        data: 'Hello World',
      );
    }
  }

  Future<void> onLoad({
    required String fontFamily,
    required double fontSize,
    required FontWeight fontWeight,
    required FontStyle fontStyle,
    required Color color,
    required bool isEnabled,
    required String data,
  }) async {
    final textStyle = GoogleFonts.getFont(
      fontFamily,
      fontSize: fontSize,
      fontWeight: fontWeight,
      fontStyle: fontStyle,
      color: color,
    );
    await GoogleFonts.pendingFonts();
    setState(() {
      _isEnabled = isEnabled;
      _data = data;
      _style = textStyle;
    });
  }

  Future<void> onEdit({
    required String fontFamily,
    required double fontSize,
    required FontWeight fontWeight,
    required FontStyle fontStyle,
    required Color color,
    required bool isEnabled,
    required String data,
  }) async {
    var updatedStyle = _style;
    updatedStyle = GoogleFonts.getFont(
      fontFamily,
      fontSize: fontSize,
      fontWeight: fontWeight,
      fontStyle: fontStyle,
      color: color,
    );
    await GoogleFonts.pendingFonts();
    setState(() {
      _isEnabled = isEnabled;
      _data = data;
      _style = updatedStyle;
    });
  }

  @override
  Widget build(BuildContext context) {
    if (!_isEnabled) {
      return SizedBox.shrink();
    }

    return Text(
      _data!,
      style: _style,
    );
  }
}

