class CameraUtils {
  Future<List<CameraDescription>> getAvailableCameras() async => await availableCameras();

  Future<CameraDescription> findBestCamera(CameraLensDirection cameraLensDirection) async {
    final cameras = await getAvailableCameras();
    return cameras.firstWhere((camera) => camera.lensDirection == cameraLensDirection);
  }

  Future<CameraController> createCameraController(CameraDescription camera, ResolutionPreset preset) async {
    return CameraController(camera, preset, enableAudio: false);
  }

  Future<bool> requestPermission() async {
    return await Permission.camera.request().isGranted;
  }
}

sealed class CameraState extends Equatable {
  const CameraState();

  @override
  List<Object> get props => [];
}

final class CameraInitial extends CameraState {}

final class CameraReady extends CameraState {
  final CameraController cameraController;

  const CameraReady({required this.cameraController});

  @override
  List<Object> get props => [cameraController];
}

final class CameraFailure extends CameraState {
  final String error;

  const CameraFailure({this.error = "CameraFailure"});

  @override
  List<Object> get props => [error];
}

final class CameraPermissionDenied extends CameraState {}

sealed class CameraEvent {}

final class CameraStopped extends CameraEvent {}

final class CameraInitialized extends CameraEvent {}

class CameraBloc extends Bloc<CameraEvent, CameraState> {
  final CameraUtils cameraUtils;
  final ResolutionPreset resolutionPreset;
  final CameraLensDirection cameraLensDirection;

  CameraController? _controller;

  CameraBloc({
    required this.cameraUtils,
    this.resolutionPreset = ResolutionPreset.high,
    this.cameraLensDirection = CameraLensDirection.back,
  }) : super(CameraInitial()) {
    on<CameraInitialized>(_onCameraInitialized);
    on<CameraStopped>(_onCameraStopped);
  }

  Future _onCameraInitialized(
      CameraInitialized event, Emitter<CameraState> emit) async {
    try {
      final isGranted = await cameraUtils.requestPermission();

      if (!isGranted) {
        emit(CameraPermissionDenied());
        return;
      }

      final camera = await cameraUtils.findBestCamera(cameraLensDirection);
      _controller =
      await cameraUtils.createCameraController(camera, resolutionPreset);
      await _controller!.initialize();
      emit(CameraReady(cameraController: _controller!));
    } on CameraException catch (error) {
      _controller?.dispose();
      emit(CameraFailure(error: error.description ?? error.toString()));
    } catch (error) {
      emit(CameraFailure(error: error.toString()));
    }
  }

  Future _onCameraStopped(
      CameraStopped event, Emitter<CameraState> emit) async {
    _controller?.dispose();
    emit(CameraInitial());
  }

  @override
  Future<void> close() {
    _controller?.dispose();
    return super.close();
  }
}

final class MockCameraDescription extends Mock implements CameraDescription {}
final class MockCameraController extends Mock implements CameraController {}
final class MockCameraUtils extends Mock implements CameraUtils {}

void main() {
  late MockCameraDescription camera;
  late MockCameraController cameraController;
  late MockCameraUtils cameraUtils;

  setUp(() {
    camera = MockCameraDescription();
    cameraController = MockCameraController();
    cameraUtils = MockCameraUtils();

    // Default responses
    when(cameraController.initialize()).thenAnswer((_) => Future.value());
    when(cameraUtils.createCameraController(camera, ResolutionPreset.max)).thenAnswer((_) => Future.value(cameraController));
