        if (!preg_match('/^\d{10}$/', $phone)) {
            echo json_encode(array("status" => "error", "message" => "Invalid phone number format. It should have exactly 10 digits."));
            die(); // Terminate script execution
        }


        $conn = connectToDatabase();

