  final encrypter = e.Encrypter(e.AES(e.Key(Uint8List.fromList(passwordSha.bytes)), mode: e.AESMode.ctr));
  final encryptedResult = encrypter.encryptBytes(
      secretContent.codeUnits,
      iv: e.IV(Uint8List.fromList(iv)));
  final encryptedText = encryptedResult.base64;

  print("encryptedText: $encryptedText");

  final passwordSha2 = sha256.convert(utf8.encode(password));
