import 'package:dio/dio.dart';
import 'package:members_info/extensions/string.dart';
import 'package:members_info/models/digital_card_model.dart';
import 'package:sentry_flutter/sentry_flutter.dart';

class NetworkDigitalCard {
  String getUrl(email) =>
      'https://acme.com/jsonCardDetails?email=$email';

// this gets called by the screen
  Future<DigitalCardModel> getDigitalCard(String email) async {
    if (!email.isValidEmail) {
      return DigitalCardModel(

          name: "Invalid Email",
          phone: "",
          expired: "",
          lapsing: "",
          externalid: 0);
    }
    try {
      Response<dynamic> response = await Dio().get(getUrl(email));
      if (response.data['unknown'] == true) {
        return DigitalCardModel(externalid: response.data['externalid']);
      }

      // good data
      return DigitalCardModel.fromJson(response.data);
    } catch (exception, stackTrace) {
      await Sentry.captureException(
        exception,
        stackTrace: stackTrace,
      );
    }
    return DigitalCardModel();
  }

  Future<DigitalCardModel> refreshContent(String email) async {
    return getDigitalCard(email);
  }
}

