Uint8List? _toPngBytesForWeb() {
   if (isEmpty) return null;
   var pColor = img.getColor(penColor.red, penColor.green, penColor.blue); //The function 'getColor' isn't defined.

   Color backgroundColor = exportBackgroundColor ?? Colors.transparent;
   var bColor = img.getColor(backgroundColor.red, backgroundColor.green, backgroundColor.blue, backgroundColor.alpha.toInt());

   double minX = double.infinity;
   double maxX = 0;
   double minY = double.infinity;
   double maxY = 0;

   // point to create fixed size
   points.add(
       Point(Offset(0 + penStrokeWidth, 0 + penStrokeWidth), PointType.tap));
   points.add(
       Point(Offset(616 + penStrokeWidth, 0 + penStrokeWidth), PointType.tap));
   points.add(Point(
       Offset(616 + penStrokeWidth, 512 + penStrokeWidth), PointType.tap));
   points.add(
       Point(Offset(0 + penStrokeWidth, 512 + penStrokeWidth), PointType.tap));
   points.add(
       Point(Offset(616 + penStrokeWidth, 0 + penStrokeWidth), PointType.tap));

   points.forEach((point) {
     minX = min(point.offset.dx, minX);
     maxX = max(point.offset.dx, maxX);

     minY = min(point.offset.dy, minY);
     maxY = max(point.offset.dy, maxY);
   });

   //point translation
   //List<Point> translatedPoints = List();
   List<Point> translatedPoints = [];
   points.forEach((point) {
     translatedPoints.add(Point(
         Offset(point.offset.dx - minX + penStrokeWidth,
             point.offset.dy - minY + penStrokeWidth),
         point.type));
   });

   var width = (maxX - minX + penStrokeWidth * 2).toInt();
   var height = (maxY - minY + penStrokeWidth * 2).toInt();

   // create the image with the given size
   img.Image cheartDrawImage = img.Image(width, height);
   // set the image background color
   img.fill(cheartDrawImage, bColor);

   // read the drawing points list and draw the image
   // it uses the same logic as the CustomPainter Paint function
   for (int i = 0; i < translatedPoints.length - 1; i++) {
     if (translatedPoints[i + 1].type == PointType.move) {
       img.drawLine( //The named parameter 'x1' is required, but there's no corresponding argument.
           cheartDrawImage,
           translatedPoints[i].offset.dx.toInt(),//Too many positional arguments: 1 expected, but 6 found.
           translatedPoints[i].offset.dy.toInt(),
           translatedPoints[i + 1].offset.dx.toInt(),
           translatedPoints[i + 1].offset.dy.toInt(),
           pColor,
           thickness: penStrokeWidth);

     } else {
       // draw the point to the image
       img.fillCircle(
           cheartDrawImage,
           translatedPoints[i].offset.dx.toInt(),
           translatedPoints[i].offset.dy.toInt(),
           penStrokeWidth.toInt(),
           img.getColor(255, 255, 255));
     }
   }
   // encode the image to PNG
   return Uint8List.fromList(img.encodePng(cheartDrawImage));
 }

