extension ListExtension<T> on List<T> {
  void place(T item, bool Function(T? prev, T curr, T? next) comparator) {
    for (int i = 0; i < length; i++) {
      T? prev;
      try {
        prev = elementAtOrNull(i - 1);
        // ignore: avoid_catching_errors
      } on RangeError {
        prev = null;
      }
      final next = elementAtOrNull(i + 1);

      final check = comparator(prev, item, next);
      if (check) {
        insert(i, item);
      }
    }
  }
}

void main() {
  bool comparator(int? prev, int curr, int? next) {
    if (next == null) {
      return true;
    }

    if (prev == null && curr < next) {
      return true;
    }

    if (curr >= prev! && curr < next) {
      return true;
    }

    if (curr >= prev && curr < next) {
      return true;
    }

    return false;
  }

  test('place', () {
    final list = [1];

    // ignore: cascade_invocations
    list.place(2, comparator);
    expect(list, [1, 2]);

    list.place(4, comparator);
    expect(list, [1, 2, 4]);

    list.place(3, comparator);
    expect(list, [1, 2, 3, 4]);
  });
}

