test('Should execute serially', () async {
  WidgetsFlutterBinding.ensureInitialized();
  final example = DoSynchronously();
  example.convertToSync();
});
class DoSynchronously {  

  static bool isExecuting = false;
  static const readDelay = 1000;

  void convertToSync() {
    while (true) {
      waitToExecute();
      isExecuting = true;
      anAsyncFunction();
    }
  }

  void waitToExecute() {
    while (isExecuting) {
      print("read delay");
      sleep(
        const Duration(milliseconds: readDelay),
      );
    }
  }

  Future<void> anAsyncFunction() async {
    startExecuting();
    await Future.delayed(Duration(seconds: 1), () => 'Delayed 2 seconds.')
        .then((result) => print(result));
    stopExecuting();
  }

  void startExecuting() {
    isExecuting = true;
    print("executing = true");
  }

  void stopExecuting() {
    isExecuting = false;
    print("Stopped executing");
  }
}

