    final authProvider = Provider.of<AuthViewModel>(context, listen: false);
    await authProvider.verifyOtp(
        context: context,
        verificationId: widget.verificationId,
        userOtp: userOtp,
        onSuccess: () async {
          // checking whether user exists in database or not
