class TimerCubit extends Cubit<int> {
  late Timer _timer;

  TimerCubit() : super(0);

  startCountDown() {
    _timer = Timer.periodic(const Duration(seconds: 1), (timer) {
      int time = state + 1;
      emit(time);
    });
  }

  stopCountDown() {
    _timer.cancel();
  }
}
void main() {
