      if(count.value == 24) {
        Get.closeAllSnackbars();
        Get.snackbar("Item Count", "You can't increase count more!");
        return;
      }
      count.value++;
      update();
  }

  void decrement(){
    if(count.value == 1)  return;
    count.value--;
    update();
  }
}

